import React, {useEffect, useState} from "react";
import { useParams } from "react-router-dom";
import { readDeck } from "../utils/api";

export default function DeckInfoComp (){

    const [deckInfo,setDeckInfo] = useState({})

    const {deckId} = useParams()



    // console.log(deckId)

    useEffect(()=>{

        async function loadData(){
            readDeck(deckId).then((response)=>setDeckInfo(response))

        }

        loadData()

    },[deckId])

    console.log(deckInfo)

    const cardsMap = deckInfo.cards.map((card,index)=>{

        return(
            <div key = {index}>
                <div>
                    {card.front}
                </div>
                <div>
                    {card.back}
                </div>
                <div>
                    <button>
                        Edit
                    </button>
                    <button>
                        delete
                    </button>
                </div>
                <hr/>
            </div>
        )

    })

    return (
        deckInfo ? (

        <div>
            <div>
                <h3>
                    {deckInfo.name}
                </h3>

                <div>
                    {deckInfo.description}
                </div>

                <div>

                    <button>
                        Edit
                    </button>

                    <button>
                        Study
                    </button>

                    <button>
                        Add Cards
                    </button>

                    <button>
                        Delete
                    </button>

                </div>

                <div>
                    <h2>
                        Cards
                    </h2>

                    <div>
                    {cardsMap}
                    </div>
                </div>
            </div>

        </div>) : (
        <div>
            Loading...
        </div>
        )
    )
}
