        }

        loadData();
    }, [deck])

    function handleChange({target}){
        setDeck({
            ...deck,
            [target.name]: target.value,
        })
    }


    return (
        <div>
            <div>
                <Link to={"/"}> Home </Link><Link>/ {deck.name} </Link>
            </div>

            <h2>
                Edit Deck
            </h2>

            <form>
                <label htmlFor="name">
                    Name

                    <textarea 
                    value = {deck.name}
                    name = "name"
                    id = "name"
                    onChange = {handleChange}
                    />
                </label>

                <label>
                    Description

                    <textarea
                    value = {deck.description}
                    name = "description"
                    id = "description"
                    onChange = {handleChange}
                    />

                </label>
            </form>
        </div>
    )
}
