import React, {useEffect, useState} from "react";
import { useParams,useHistory } from "react-router-dom";
import { readDeck, deleteCard } from "../utils/api";


export default function DeckInfoComp (){

    const initialization = {
        deck: [],
        cards: [1,2,3],
    }

    const [deckInfo,setDeckInfo] = useState(initialization)


    const {deckId} = useParams()
    const history = useHistory()

    useEffect(()=>{

        async function loadData(){
            const response = await readDeck(deckId)
            setDeckInfo(response)

        }

        loadData()

    },[deckId])

    const cardsMap = deckInfo.cards.map((card,index)=>{

        function handleEdit(){
            history.push(`/decks/${card.deckId}/cards/${card.id}`)
        }

        function handleDelete(){
            if(window.confirm("Are you sure?")){
                deleteCard(card.id)
                window.location.reload(true)
            }

        }

        return(
            <div key = {index}>
                <div>
                    Front: {card.front}
                </div>
                <div>
                    Back: {card.back}
                </div>
                <div>
                    <button onClick={handleEdit}>
                        Edit
                    </button>
                    <button onClick={handleDelete}>
                        delete
                    </button>
                </div>
                <hr/>
            </div>
        )

    })

    if (deckInfo.cards[0] == 1) return "Loading";

    return (
        deckInfo ? (

        <div>
            <div>
                <h3>
                    {deckInfo.name}
                </h3>

                <div>
                    {deckInfo.description}
                </div>

                <div>

                    <button>
                        Edit
                    </button>

                    <button>
                        Study
                    </button>

                    <button>
                        Add Cards
                    </button>

                    <button>
                        Delete
                    </button>

                </div>

                <div>
                    <h2>
                        Cards
                    </h2>

                    <div>
                        {cardsMap}
                    {/* <CardMapping deckInfo = {deckInfo}/> */}
                    </div>
                </div>
            </div>

        </div>) : (
        <div>
            Loading...
        </div>
        )
    )
}
