    const [deckInfo,setDeckInfo] = useState({})

    const {deckId} = useParams()

    let cardsMap;

    console.log(deckId)

    useEffect(()=>{

        async function loadData(){
            readDeck(deckId).then((response)=>setDeckInfo(response))
            .then(console.log(deckInfo.cards))

        }

        loadData()

    },[deckId])



    return (
        deckInfo ? (

        <div>
            <div>
                <h3>
                    {deckInfo.name}
                </h3>

                <div>
                    {deckInfo.description}
                </div>

                <div>

                    <button>
                        Edit
                    </button>

                    <button>
                        Study
                    </button>

                    <button>
                        Add Cards
                    </button>

                    <button>
                        Delete
                    </button>

                </div>

                <div>
                    <h2>
                        Cards
                    </h2>

                    <div>

                    </div>
                </div>
            </div>

        </div>) : (
        <div>
            Loading...
        </div>
        )
    )
}
